function Tab(){this.ulis=document.querySelectorAll("ul>li"),this.olis=document.querySelectorAll("ol>li")}Tab.prototype.change=function(){this.ulis.forEach((i,s)=>{i.onclick=()=>{this.ulis.forEach(i=>i.classList.remove("active")),this.ulis[s].classList.add("active"),this.olis.forEach(i=>i.classList.remove("active")),this.olis[s].classList.add("active")}})};let tab=new Tab;tab.change();